import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"; // Adjust imports as necessary
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"; // Adjust imports as necessary
import { Button } from "@/components/ui/button"; // Adjust imports as necessary
import { Factory, X } from 'lucide-react'; // Adjust imports as necessary

const SupplyChainBuilder = () => {
  const [facilitiesData, setFacilitiesData] = useState({});
  const [selectedStage, setSelectedStage] = useState('mining');

  useEffect(() => {
    fetch('/api/facilities')
      .then(response => response.json())
      .then(data => setFacilitiesData(data));
  }, []);

  const FacilitySelector = ({ stage, facilities }) => (
    <div className="mb-6">
      <div className="flex items-center justify-between mb-2">
        <h3 className="font-semibold">{stage.charAt(0).toUpperCase() + stage.slice(1)}</h3>
      </div>
      <Select onValueChange={(value) => console.log(value)}>
        <SelectTrigger>
          <SelectValue placeholder="Select facility" />
        </SelectTrigger>
        <SelectContent>
          {facilities.map((facility) => (
            <SelectItem key={facility.id} value={facility.id}>
              {facility.name} - {facility.company}
            </SelectItem>
          ))}
        </SelectContent>
      </Select>
    </div>
  );

  return (
    <div>
      {Object.entries(facilitiesData).map(([stage, facilities]) => (
        <FacilitySelector key={stage} stage={stage} facilities={facilities} />
      ))}
    </div>
  );
};

export default SupplyChainBuilder;
